  var headerObj = {};
  (function getHeader(){
	var req = new XMLHttpRequest();
	if(window.XDomainRequest){
		req.open('GET', document.location);
		req.onreadystatechange = function(){
			if(req.readyState != 4){return false;}
			if(req.status != 200){return false;}
			headersToObj(req.getAllResponseHeaders());
			return true;
		}
	  }else{
		req.open('HEAD', document.location, true);
		req.onload = function(e){
			if(req.readyState === 4)  {
			  if(req.status === 200) {
				  headersToObj(req.getAllResponseHeaders());
				}
			  }
		};  
	}
	req.send(null);
  })();

  function headersToObj(headerStr){
	var headerStr = String(headerStr).toLowerCase().split("\r\n");
	for(index in headerStr){
		headerArr = headerStr[index].split(": ");
		if(headerArr[1]){headerObj[headerArr[0]] = headerArr[1];}
	}
  }

  Object.size = function(obj) {
	var size = 0, key;
	for (key in obj) {
		if (obj.hasOwnProperty(key)) size++;
	}
	return size;  
  };

  var headerInterval = setInterval(function(){
	if(Object.size(headerObj)){
		clearInterval(headerInterval);
		tls_version();
	}
  },500);

