function toggleCatalogData(series_id) {
    var label = $("#s_" + series_id).text();
    if (label == "+") {
        $("#s_" + series_id).text("–");
        $("#s_" + series_id).attr('title', 'Hide catalog data');
    } else {
        $("#s_" + series_id).text("+");
        $("#s_" + series_id).attr('title', 'View catalog data');
    }
    $("#catalog_" + series_id).slideToggle("fast"); 
}

