function removeByValue(arr, val) {     
    for(var i=0; i<arr.length; i++) {   
        if(arr[i] == val) {   
            arr.splice(i, 1);
            break;   
       }
    }
      amplify.store(val, null);
} 


// Function invoked on check\uncheck of check box
function check(checkbox)
{
    var checkboxValue = checkbox.value;

    if(checkbox.checked == true)
    {
        // check if series already present in list
        if($.inArray(checkboxValue, selectedSeriesId) == -1)
        {
            // append selected series id to list

            selectedSeriesId.push(checkboxValue); 
            var seriesTitle = $(checkbox).data('title');            
            amplify.store(checkboxValue, unescape(seriesTitle));
            amplify.store(checkboxValue, seriesTitle);
        }

    }
    else
    {       
        // check if series already present in list
        if($.inArray(checkboxValue, selectedSeriesId) != -1)
        {
            removeByValue(selectedSeriesId,checkboxValue);      
        }
    }
    amplify.store('selectedSeriesId' , selectedSeriesId.join("||"));
}

