// Function that dynamically creates form from URL and submits form
function convertGetToPost(link)
{
    var originalURL = link.attr('href');
    // Extra amp; is being used in pagination links thts causing problem
    // Need to identify source.
    originalURL = originalURL.replace(/amp%3B/g,"");
    // Set original URL as action so it can be book marked
    var form = $(document.createElement('form')).attr('action', originalURL).attr('method','post');

    // store the seriesids in jquery session
    amplify.store('selectedSeriesId' , selectedSeriesId.join("||"));

    //Append Selected and Deselected Series Id to link    
    var modLink = link.attr('href') + "&selectedseriesid=" + selectedSeriesId.join("||");
    link.attr('href', modLink);

    // decode the link URL    
    var decodedURL = decodeURIComponent(link.attr('href'));
    decodedURL = decodedURL.replace(/&/g,"&");    

    var p = decodedURL.split('?');
    var action = p[0];
    var params = p[1].split('&');    

    // Appen Form to Html Body
    $('body').append(form);

    for (var i in params) 
    {
        var tmp= params[i].split('=');
        var key = tmp[0], value = tmp[1];            
        // Create form hidden fields from url parameters
        $(document.createElement('input')).attr('type', 'hidden').attr('name', key).attr('value', value).appendTo(form);
    }       

    $(form).submit();
}

